import React from 'react';
import { Link } from 'react-router-dom';

const ChartTemplatesPage = () => {
    const templates = [
        { id: 1, name: 'Sales Bar Chart', description: 'Visualize sales data with a simple bar chart.' },
        { id: 2, name: 'Revenue Pie Chart', description: 'Display revenue breakdown in a pie chart.' },
        { id: 3, name: 'Line Trend Analysis', description: 'Show trends over time with a line chart.' },
        { id: 4, name: 'Donut Budget Chart', description: 'Visualize budget allocation with a donut chart.' }
    ];

    return (
        <div className="container py-5">
            <h1 className="display-4 text-center mb-4">Chart Templates</h1>
            <div className="row row-cols-1 row-cols-md-2 g-4">
                {templates.map((template) => (
                    <div key={template.id} className="col">
                        <div className="card h-100 shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">{template.name}</h5>
                                <p className="card-text">{template.description}</p>
                                <Link to={`/chart-builder/${template.id}`} className="btn btn-primary">Use Template</Link>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ChartTemplatesPage;
